import React from 'react'

const ConversationItem = ({ time, message, onClick }) => {
    return (
        <div>
            <div className={'conversation-item p-1 bg-[#171717] hover:bg-[#212121] m-1 rounded-md'} onClick={onClick} >
                <div className={'flex items-center p-2  cursor-pointer  '}>
                    <div className="flex-grow p-2">
                        <div className="text-sm text-white  w-40 truncate">
                            {message}
                        </div>
                        <div className="flex justify-between text-md ">
                            <div className="text-xs text-gray-400 dark:text-gray-300">{time}</div>
                        </div>

                    </div>
                    <button
                        onClick={(e) => {
                            e.stopPropagation(); // Prevent triggering onClick for the parent
                            onDelete();
                        }}
                        className="text-red-500 hover:text-red-700 text-xs px-2 py-1 rounded"
                    >
                        Delete
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ConversationItem
